mapper.update:
update user_info
set user_status=#{userStatus}
where user_account = #{userAccount};

update user_info
set user_if_del=1
where user_account = #{userAccount};

update user_info
set user_pwd=#{userPwd}
where user_account = #{userAccount};

mapper.select:
select *
from user_info


        and user_account like concat('%', #{userNameSearch}, '%')


        and user_if_del=#{userIfDel}


limit #{lim}, #{off};

select count(*) from user_info
and user_account like concat('%', #{userNameSearch}, '%')
and user_if_del=#{userIfDel}

select *
from user_info


        and user_account like concat('%', #{userNameSearch}, '%')


        and user_status=#{userStatus}


limit #{lim}, #{off};

select count(*) from user_info
and user_account like concat('%', #{userNameSearch}, '%')
and user_status=#{userStatus}
